mapper.update:
UPDATE users_tb 
SET user_name = #{userName}, 
    birth = #{birth}, 
    address = #{address}, 
    detail_address = #{detailAddress} 
    WHERE uid = #{uid}

mapper.select:
select user_name as userName, address, detail_address as detailAddress, birth, phone_number as phoneNumber
from users_tb
where uid = #{uid}
